import React, { Component } from 'react';
import './main.css';

export default class Main extends Component {
    state = {//Declara variaveis globais
        titulo: "Game Fifteen",
        box: [""]
    };

    componentDidMount() { //Metodo que executa ao carregar componente
        this.loadBox(); //Inicia o metodo
    }

    loadBox = () => {//Metodo que gera elementos
        const { box } = this.state;
        var num, min = 1, max = 16, i = 0;
        while (i <= 15) {
            min = Math.ceil(min);
            max = Math.floor(max);
            num = Math.floor(Math.random() * (max - min + 1)) + min; // eslint-disable-next-line
            if (num !== (box.find(value => value === num))) {
                box[i] = num;
                i++;
            }
        }
        box.map((value, index) => box[index] = (value < 10) ? String("0" + value) : String(value));
        box.map((value, index) => box[index] = (value === "16") ? String(" ") : String(value));
        this.setState({ box });
    };

    playBox = ({ currentTarget }) => {
        const { box } = this.state;
        var id = currentTarget.id, pos, value = currentTarget.innerHTML;
        value = value.replace("<strong>", "");
        value = value.replace("</strong>", "");
        box.find((value, index) => (value === " ") ? pos = index : false);// eslint-disable-next-line
        if ((pos - 1 == id) || (pos + 1 == id) || (pos - 4 == id) || (pos + 4 == id)) {
            box[id] = " ";
            box[pos] = value;
            this.setState({ box });
        }
    }

    render() {
        const { titulo, box } = this.state;
        // console.log("Render: " + box);
        return (
            <div className="border">
                <h1>
                    {titulo}
                </h1>
                {box.map((values, index) => (
                    <div key={index} id={index} className="box" onClick={this.playBox}><strong>{values}</strong></div>
                ))
                }
            </div>
        )
    }
}
