import React, { Component } from 'react';
import './main.css';

export default class Main extends Component {
    state = {
        titulo: "Game Fifteen",
        box: ["01", "02", "03", "04", "05", "06", "07", "08", "09", "10", "11", "12", "13", "14", "15", " "]
        // box: []
    };

    componentDidMount() { //Metodo que executa ao carregar componente
        this.loadBox(); //Inicia o metodo
    }

    loadBox = () => {
        const { box } = this.state;
        var num, min = 1, max = 16, i = 0;
        while (i <= 15) {
            min = Math.ceil(min);
            max = Math.floor(max);
            num = Math.floor(Math.random() * (max - min + 1)) + min;
            if (num !== (box.find(value => value === num))) {
                box[i] = num;
                i++;
            }
        }
        box.map((value, index) => box[index] = ((value.length) <= "1") ? String(value) : String("0" + value));
        console.log(box[1].length);
        this.setState({ box });
    };

    render() {
        const { titulo, box } = this.state;
        console.log("Render: " + box);
        return (
            <div className="border">
                <h1>
                    {titulo}
                </h1>
                {box.map((values, index) => (
                    <div key={index} className="box"><strong>{values}</strong></div>
                ))}
            </div>
        )
    }
}
