import React, { Component } from 'react';
import './main.css';

export default class Main extends Component {
    //Declara variaveis globais
    state = {
        titulo: "Game Fifteen",
        subTitulo: "Ordene os campos",
        win: "jogando",
        box: [""],
        contagem: 0,
    };
    //Metodo que executa ao carregar componente
    componentDidMount() {
        //Inicia o metodo
        this.loadBox();
    }

    //Metodo que gera jogo aleatorio
    loadBox = () => {
        //Carrega variaveis do state
        const { box } = this.state;
        let { contagem, win } = this.state;
        var num, min = 1, max = 16, i = 0;
        //Gera posicoes aleatorias
        while (i <= 15) {
            min = Math.ceil(min);
            max = Math.floor(max);
            num = Math.floor(Math.random() * (max - min + 1)) + min;
            //verifica se nymero gerado ja existe
            // eslint-disable-next-line 
            if (num !== (box.find(value => value === num))) {
                box[i] = num;
                i++;
            }
        }
        //Converte numeros em string e preenche 16 como vazio.
        box.map((value, index) => box[index] = (value < 10) ? String("0" + value) : String(value));
        box.map((value, index) => box[index] = (value === "15") ? String(" ") : String(value));
        box.map((value, index) => box[index] = (value === "16") ? String("15") : String(value));
        //Zera cotagem
        contagem = 0;
        win = "jogando";
        //Salva state das variaveis
        this.setState({ box, contagem, win });
    };

    //Metodo de jogar
    playBox = ({ currentTarget }) => {
        //Carrega variaveis do state
        const { box } = this.state;
        let { contagem } = this.state;
        //Carrega propriedades do elemento clicado
        var id = currentTarget.id, value = currentTarget.innerHTML;
        //Remove elemento <strong>
        value = value.replace("<strong>", "");
        value = value.replace("</strong>", "");
        //Localiza posicao do campo de valor vazio
        // box.find((value, index) => (value === " ") ? pos = index : false);
        var pos = box.findIndex((value) => (value === " "));

        //Valida de a jogada eh possivel 
        //(somente campos anterior, proximo, superior e inferior podem jogar)
        // eslint-disable-next-line
        if ((pos - 1 == id) || (pos + 1 == id) || (pos - 4 == id) || (pos + 4 == id)) {
            //Salva novos valores do campo jogado
            box[id] = " ";
            box[pos] = value;
            contagem++;
            //Salva novo state das variaveis
            this.setState({ box, contagem });
            this.winBox();
        }
    }

    //Verifica se Ganhou
    winBox = () => {
        //Importa variaveis de state
        const { box } = this.state;
        let { win } = this.state;
        let corretos = 0;
        //Cria jogo campeao
        const full = ["01", "02", "03", "04", "05", "06", "07", "08", "09", "10", "11", "12", "13", "14", "15", " "];
        //Conta os campos corretos
        box.map((value, index) => value === full[index] ? corretos++ : false);
        //Verifica se todos so 16 campos estiverem na posicao
        win = corretos === 16 ? win = "congrats" : "jogando";
        //Salva variavel ganhar
        this.setState({ win });
        // this.render();
    }

    autoBox = () => {
        const { box } = this.state;
        let { contagem } = this.state;
        var vazio, num, fifteen = 1, corretos = 0, go = 0, lvazio, lnum;

        while (fifteen <= 1) {
            (fifteen < 10) ? fifteen = String("0" + fifteen) : fifteen = String(fifteen);
            // eslint-disable-next-line
            num = box.findIndex(value => value === fifteen);
            vazio = box.findIndex(value => value === " ");
            go = num;

            var dif = vazio - num;
            // setTimeout(() => { }, 1000);
            while (go < 20) {
                console.log("> " + num + " > " + vazio + " > " + dif + " > " + go);
                switch (num) {
                    case (num <= 3):
                        lnum = 1;
                        break;
                    case (num >= 4) && (num <= 7):
                        lnum = 2;
                        break;
                    case (num >= 8) && (num <= 11):
                        lnum = 3;
                        break;
                    case (num >= 12):
                        lnum = 4;
                        break;
                    default:
                        break;
                }
                switch (vazio) {
                    case (vazio <= 3):
                        lvazio = 1;
                        break;
                    case (vazio >= 4) && (vazio <= 7):
                        lvazio = 2;
                        break;
                    case (vazio >= 8) && (vazio <= 11):
                        lvazio = 3;
                        break;
                    case (vazio >= 12):
                        lvazio = 4;
                        break;
                    default:
                        break;
                }
                console.log("> " + lvazio + " > " + lnum);
                if (lnum !== lvazio) {
                    switch (dif) {
                        case (dif < -4):
                            box[vazio] = box[vazio + 4];
                            box[vazio + 4] = " ";
                            contagem++;
                            lvazio++;
                            break;
                        case (dif > 4):
                            box[vazio] = box[vazio - 4];
                            box[vazio - 4] = " ";
                            contagem++;
                            lvazio--;
                            break;
                        default:
                            break;
                    }
                }
                if (lvazio !== lnum) {
                    switch (dif) {
                        //Depois
                        case (dif <= -1) && (dif >= -3):
                            box[vazio] = box[vazio + 1];
                            box[vazio + 1] = " ";
                            contagem++;
                            break;
                        //Antes
                        case 1:
                            return go = vazio;
                        case (2 >= dif) && (dif <= 3):
                            box[vazio] = box[vazio - 1];
                            box[vazio - 1] = " ";
                            contagem++;
                            break;
                        default:
                            break;
                    }
                    this.setState({ box, contagem });
                }
                go++;
            }

            fifteen = parseInt(fifteen);
            fifteen++;
            this.winBox();
        }
    }

    //Metodo para carregar(renderizar) a pagina
    render() {
        //Carrega variaveis do state
        const { titulo, subTitulo, box, win, contagem } = this.state;
        // Cria a pagina
        return (
            <div className="border">
                <h1> {titulo} </h1>
                <h3> {subTitulo} </h3>
                <div className={win}></div>
                {box.map((values, index) => (
                    <div key={index} id={index} className="box" onClick={this.playBox}><strong>{values}</strong></div>
                ))}
                <div>
                    <button onClick={this.loadBox}><strong>Reiniciar</strong></button>
                    <button onClick={this.autoBox} disabled={win === "congrats"}><strong>Auto</strong></button>
                    <span>Jogadas: {contagem}</span>
                </div>
            </div>
        )
    }
}
